const socket = io.connect(window.location.origin);
const video = document.querySelector("video");
const toggleAudioButton = document.querySelector("#toggle-audio");

toggleAudioButton.addEventListener("click", toggleAudio)

socket.on("offer", (id, description) => {
  peerConnection = new RTCPeerConnection(config);
  peerConnection
    .setRemoteDescription(description)
    .then(() => peerConnection.createAnswer())
    .then(sdp => peerConnection.setLocalDescription(sdp))
    .then(() => {
      socket.emit("answer", id, peerConnection.localDescription);
    });
  peerConnection.ontrack = event => {
    video.srcObject = event.streams[0];
  };
  peerConnection.onicecandidate = event => {
    if (event.candidate) {
      socket.emit("candidate", id, event.candidate);
    }
  };
});


socket.on("candidate", (id, candidate) => {
  peerConnection
    .addIceCandidate(new RTCIceCandidate(candidate))
    .catch(e => console.error(e));
});

socket.on("connect", () => {
  socket.emit("watcher");
});

socket.on("update-prediction", (msg) => {
  document.querySelector('#prediction').textContent = `Received: ${msg}`;
})

socket.on("broadcaster", (msg) => {

  // DK
  document.querySelector('video').classList.remove('visibility-hidden');

  socket.emit("watcher");
});

socket.on("disconnect-watcher", () => {
  document.querySelector('video').classList.add('visibility-hidden');
})

window.onunload = window.onbeforeunload = () => {
  socket.close();
  peerConnection.close();
};

function toggleAudio() {
  if (!!video.muted) {
    video.muted = false;
    toggleAudioButton.textContent = "Disable Audio";
  } else {
    video.muted = true;
    toggleAudioButton.textContent = "Enable Audio";
  }
}

